import { SET_GAME, GET_ALL_GAMES } from '../types';

export default function gameReducer(state = null, action) {
  const { type, payload } = action;
  switch (type) {
    case GET_ALL_GAMES:
      return payload;
    case SET_GAME:
      return payload;
    default:
      return state;
  }
}
