import { createSlice } from '@reduxjs/toolkit';

const countQuestionsSlice = createSlice({
  name: 'products',
  initialState: 0,
  reducers: {
    setCountQuestions: (state, action) => ++state,
    resetCountQuestions: (state, action) => 0,
  },
});

export const { setCountQuestions, resetCountQuestions } = countQuestionsSlice.actions;

export default countQuestionsSlice.reducer;
