import { createSlice } from '@reduxjs/toolkit';

const userScoreSlice = createSlice({
  name: 'products',
  initialState: 0,
  reducers: {
    upScore: (state, action) => state += action.payload,
    downScore: (state, action) => state -= action.payload,
  },
});

export const { upScore, downScore } = userScoreSlice.actions;

export default userScoreSlice.reducer;
