import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import { useDispatch, useSelector } from 'react-redux';
import { downScore, upScore } from '../../../redux/slices/userScoreSlice';
import { setQuestions } from '../../../redux/slices/questionSlice';
import { setCountQuestions } from '../../../redux/slices/countQuestionsSlice';

export default function Question({ question }) {
  const [showTimeoutId, setshowTimeoutId] = useState(null);
  const [show, setShow] = useState(false);
  const [input, setInput] = useState('');
  const [answerColor, setAnswerColor] = useState(null);
  const [disabled, setDisabled] = useState('');
  // const userScore = useSelector((state) => state.userScore);
  const dispatch = useDispatch();

  useEffect(
    () => {
      if (show) {
        const id = setTimeout(() => {
          setDisabled('disabled');
          setShow(false);
        }, 15000);
        setshowTimeoutId(id);
      } return () => {
        dispatch(downScore(question.price));
        clearTimeout(showTimeoutId);
      };
    },
    [show],
  );

  const setColor = (answer) => {
    switch (answer) {
      case true:
        setAnswerColor('green');
        dispatch(upScore(question.price));
        break;
      case false:
        setAnswerColor('red');
        dispatch(downScore(question.price));
        break;
      default:
        setAnswerColor('blue');
        break;
    }
  };

  const handleClose = () => {
    setColor(input === question.ans);
    setInput('');
    setShow(false);
    dispatch(setCountQuestions());
    setDisabled('disabled');
  };
  const handleShow = () => setShow(true);

  return (
    <>
      <Button style={{ backgroundColor: answerColor }} disabled={disabled} variant="primary" onClick={handleShow}>
        {question.price}
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>ответьте на вопрос</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <h2>{question.body}</h2>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>ваш ответ</Form.Label>
              <Form.Control
                name="ans"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                as="textarea"
                rows={3}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            ЗАКРЫТЬ
          </Button>
          <Button variant="primary" onClick={handleClose}>
            ОТВЕТИТЬ
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
