import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { redirect } from 'react-router-dom';
import { Button } from 'reactstrap';

import { setGameThunk } from '../../../redux/actions/GameAction';

export default function GameOver() {
  const dispatch = useDispatch();
  const score = useSelector((store) => store.userScore);
  const user = useSelector((store) => store.user);
  useEffect(() => {
    console.log(score);
    dispatch(setGameThunk(user.id, score));
  }, []);

  const clickHandler = () => {
    redirect('/');
  };
  return (
    <div>
      <p>
        GameOver,
        {' '}
        {user.name}
      </p>
      <p>
        {' '}
        {score}
      </p>
      <Button onClick={(e) => clickHandler(e)}> New Game</Button>
    </div>
  );
}
