import { configureStore } from '@reduxjs/toolkit';
import userReducer from './reducers/userReduser';
import errReducer from './reducers/errReducer';
import gameReduser from './reducers/gameReduser';
import questionSlice from './slices/questionSlice';
import userScoreSlice from './slices/userScoreSlice';
import countQuestionsSlice from './slices/countQuestionsSlice';

const store = configureStore({
  reducer: {
    user: userReducer,
    err: errReducer,
    games: gameReduser,
    questions: questionSlice,
    userScore: userScoreSlice,
    countQuestions: countQuestionsSlice,
  },
});

export default store;
