import axios from 'axios';
import { GET_ALL_GAMES } from '../types'; // SET_GAME,

export const getingHistory = (payload) => ({ type: GET_ALL_GAMES, payload });
export const setGame = (payload) => ({ type: GET_ALL_GAMES, payload });

export const getGamesThunk = () => (dispatch) => {
  axios('/game/all')
    .then((res) => dispatch(getingHistory(res.data)));
};

export const setGameThunk = (id, score) => (dispatch) => {
  axios.put('/game/new', { id, score })
    .then((res) => dispatch(setGame(res.data)));
};
